package Assignment;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  for(int i=1; i<10; i++)// initialization
			  {
			  System.out.println(i); 
			  }
		  int i=1;
		  while(i<=10); { System.out.println(i); 
		  i++;
		  
		  }
		  
		 
		int i1 = 0;
		do {

			System.out.println(i1);
			i1++;
		} while (i1 <= 10);

	}

}
