package Practice1;

public class Maxvalueprint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[]= {55,24,5,6,44,89};
		int max = a[0]; //assume 
		for(int i=0; i<a.length; i++) {
			if(a[i]>max) {
				max=a[i];				
			}			
		}
			System.out.println("The max value is "  +max);
		
			System.out.println("---------------");
	}
}
           
		
		
		
		
			
			
		
		
		 

	


