package package1;

public class Incrementoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		System.out.println(a);
		a++;//post increment
		System.out.println(a);
		++a;//pre increment
		System.out.println(a);
		System.out.println(a++);
		a++;
		System.out.println(a);
		System.out.println(a++);
		System.out.println(a);
		System.out.println(++a);
		System.out.println(a);

	}

}
